package deviprasad.aug14;

public class Java10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for (int i = 1; i <=4; i++) {
			
			for (int j =1 ; j <=i; j++) {
				
				System.out.print("*");
			}
			System.out.println();
			
			for (int u = 1; u <=4; u++) {
				
				for (int j =3 ; j >=u; j--) {
					
					System.out.print("*");
				}
				System.out.println();
				
			
		}
	}

}
}